select distinct e.id as enhancer_id, e.chrom as chrom, e.startcoords as startcoords, e.endcoords as endcoords, e.name as enhancer_name, e.soxbindpattern as soxbindpattern, e.transcomp as transcomp, d.startcoord as dhs_start, d.endcoord as dhs_end, d.stage as stage  
from enhancer e, dnase_site d where e.chrom=d.chrom AND ((e.startcoords <= d.startcoord AND e.endcoords >= d.startcoord) OR (e.startcoords <= d.endcoord AND e.endcoords >= d.endcoord) OR (e.startcoords >= d.startcoord AND e.endcoords <= d.endcoord)) AND (e.soxbindpattern='Dunique' OR e.soxbindpattern='SoxNunique' OR e.soxbindpattern='common');